      };
      Number.prototype.withCommas = function () {
        var e = 6
        , t = parseFloat(this)
        .toFixed(0)
        .toString()
        .reverse();
        while (e < t.length) {
          t = t.substring(0, e) + "," + t.substring(e);
          e += 4
        }
        return t.reverse()
      };
      Number.prototype.toCurrency = function () {
        return this.withCommas() + (arguments[0] ? arguments[0] : "$")
      };
