	$(document).on('click','.quantity-selector .entry', function(){
		var _qty = parseInt($('.quantity-selector .entry.number').text());
		if($(this).hasClass('number-minus')){
			if(_qty > 1){
				$('.quantity-selector .entry.number').html(_qty - 1);
			}
		}else{
			$('.quantity-selector .entry.number').html(_qty + 1);
		}
	})
