    $(document).ready(function() {
        setTimeout(function() {
            $('.swatch .select-swap .swatch-element:first-child input').trigger('click');
        }, 200)
        if ($(window).width() > 1024) {
            jQuery(".product-image-feature").elevateZoom({
                scrollZoom: true,
                easing: true
            });
        }
    })


      //

	var callBack = function (variant, selector) {
		if (variant) {
			modal = $('#quick-view-modal');
			if (variant.featured_image) {  
				var _imgSRC = variant.featured_image.src;
				var _urlIMG = '';
				if(_imgSRC.indexOf('-1')){
					_imgSRC = _imgSRC.split('?');
					_urlIMG = _imgSRC[0];
				}else{
					_urlIMG = _imgSRC;
				}
				setTimeout(function(){
					modal.find('.product-thumb a[data-image="'+Bizweb.resizeImage(_urlIMG, 'medium')+'"] img').trigger('click');
				},300)
			}
			if(variant.price > 0 ){
				modal.find('.noPrice').hide();
				modal.find('.addToCartModal').show();
				modal.find('.qv-readmore > span').show();
				modal.find('.qty').show();
				modal.find('.p-price').html(Bizweb.formatMoney(variant.price, "{{amount_no_decimals_with_comma_separator}}?"));
				if (variant.compare_at_price > variant.price )
					modal.find('del').html(Bizweb.formatMoney(variant.compare_at_price, "{{amount_no_decimals_with_comma_separator}}?"));
				else
					modal.find('del').html('');
			}else{
				modal.find('.p-price').html('Liên h?');
				modal.find('.addToCartModal').hide();
				modal.find('.qty').hide();
				//modal.find('.noPrice').show();
				modal.find('.qv-readmore > span').hide();
				modal.find('del').html('');
			}
			if (variant.available) {
				modal.find('.addToCartModal').removeClass('disabled').removeAttr('disabled').html('Thêm vào gi?');
				//modal.find('.btn-soldout').css('display', 'none');
			}
			else {
				modal.find('.addToCartModal').addClass('disabled').attr('disabled','disabled').html('H?t hàng');
				//modal.find('.btn-soldout').css('display', 'block');
			}
			if (variant.sku != null ){
				modal.find('.m-sku').html('<span>Mã s?n ph?m: </span>'+ variant.sku).show();
			}else{
				modal.find('.m-sku').hide();
			}
		}
		else {
			modal.find('.addToCartModal').addClass('disabled').attr('disabled','disabled').html('H?t hàng').show();
			modal.find('.p-price').html('H?t hàng');
			modal.find('.qty').hide();
			modal.find('del').html('');
		}
	}
	var p_select_data = $('.p-option-wrapper').html();
	var p_zoom = $('.image-zoom').html();
	var quickViewProduct = function (purl) {
		if ($(window).width() < 680) { window.location = purl; return false; }
		modal = $('#quick-view-modal'); modal.modal('show');
		$.ajax({
			url: purl + '.js',
			async: false,
			success: function (product) {
				$.each(product.options, function (i, v) {
					product.options[i] = v.name;
				})
				modal.find('.addToCartModal').removeClass('disabled').removeAttr('disabled').html('Thêm vào gi?');
				modal.find('.p-title').html('<h3>' + product.name + '</h3>');
				modal.find('.p-option-wrapper').html(p_select_data);
				if(product.vendor != null){
					modal.find('.m-vendor').html('<span>Nhà cung c?p: </span>'+ product.vendor).show();
				}else{
					modal.find('.m-vendor').hide();
				}
				var productdes = product.description;
				if(productdes == '' || productdes == 'undefined' || productdes == null ){
					productdes = product.content;
				}
				if(productdes != '' && productdes != 'undefined' && productdes != null ){
					var re_productdes = productdes.replace(/(<([^>]+)>)/ig,"")
					var des = re_productdes.split("&nbsp;").splice(0, 30).join(" ") + "...";
					modal.find('.form-des').html(des);
					modal.find('.form-des').show();
				}else{
					modal.find('.form-des').html('Chua có mô t? cho s?n ph?m này!');
				}
				$('.image-zoom').html(p_zoom);
				modal.find('.p-url').attr('href', product.url);
				if (product.images.length == 0) {
					modal.find('.p-product-image-feature').attr('src', '//hstatic.net/0/0/global/noDefaultImage6_large.gif');
				}
				else {
					$('#p-sliderproduct').remove();
					$('.image-zoom').append("<div id='p-sliderproduct' class='flexslider'>");
					$('#p-sliderproduct').append('<ul class="slides owl_design"></ul>');

					$.each(product.images, function (i, v) {
						elem = $('<li class="product-thumb">').append('<a href="#" data-image="" data-zoom-image=""><img /></a>');
						elem.find('a').attr('data-image', Bizweb.resizeImage(v, 'medium'));
						elem.find('a').attr('data-zoom-image', v);
						elem.find('img').attr('data-image', Bizweb.resizeImage(v, 'medium'));
						elem.find('img').attr('data-zoom-image',v);
						elem.find('img').attr('src', Bizweb.resizeImage(v, 'small'));
						modal.find('.slides').append(elem);
					})

					modal.find('.p-product-image-feature').attr('src', product.featured_image);
					$(".modal-footer .btn-readmore").attr('href', purl);
					setTimeout(function(){
						$('#p-sliderproduct .slides').owlCarousel({
							items: 4,
							loop: false,
							autoplay: false,
							margin: 10,
							responsiveClass: true,
							nav : true,
							navText: ['' , ''],
							responsive: {
								0: {
									items: 1,
									margin: 0
								},
								414: {
									items: 2,
									margin: 15
								},
								480: {
									items: 2,
									margin: 15
								},
								768: {
									items: 2,
									margin: 15
								},
								992: {
									items: 4
								}
							}
						});
						modal.find('.owl-item:first-child .product-thumb img').click();
					},100)
				}
				$.each(product.variants, function (i, v) {
					modal.find('select#product-selectors').append("<option value='" + v.id + "'>" + v.title + ' - ' + v.price + "</option>");
				})
				if (product.variants.length == 1 || product.variants[0].title.indexOf('Default') != -1){
					$('.p-option-wrapper').hide();
					callBack(product.variants[0], null);
				}
				else
					//$('.p-option-wrapper').hide();

					if (product.variants.length == 1 && product.variants[0].title.indexOf('Default') != -1) {
						callBack(product.variants[0], null);
					}
				else {
					new Bizweb.OptionSelectors('product-selectors', {
						product: product,
						onVariantSelected: callBack, 
						enableHistoryState: true
					});     
					//debugger
					if (product.options.length == 1 && product.options[0].indexOf('Tiêu d?')==-1)

						modal.find('.selector-wrapper:eq(0)').prepend('<label>' + product.options[0] + '</label>');
					$('.p-option-wrapper').show();
					$('.p-option-wrapper select:not(#product-selectors)').each(function () {
						$(this).wrap('<span class="custom-dropdown custom-dropdown--white"></span>');
						$(this).addClass("custom-dropdown__select custom-dropdown__select--white");
					});
					callBack(product.variants[0], null);
				}
			}
		});

		//$('.modal-backdrop').css('opacity', '0');
		return false;
	}
	$('#quick-view-modal ').on('click', '.product-thumb img', function (event) {
		event.preventDefault();
		modal = $('#quick-view-modal');
		modal.find('.p-product-image-feature').attr('src', $(this).attr('data-zoom-image'));
		modal.find('.product-thumb').removeClass('active');
		$(this).parents('li').addClass('active');
		return false;
	})
	$(document).on('click', 'a.btn-quickview-1', function (event) {
		//console.log('abc')
		event.preventDefault();
		quickViewProduct($(this).attr('data-alias'));
	})

